use std::io;

fn main() {
    println!("Simple Calculator");
    println!("Enter an expression (e.g., 5 + 3):");

    let mut input = String::new();
    io::stdin().read_line(&mut input).expect("Failed to read line");

    let parts: Vec<&str> = input.trim().split_whitespace().collect();

    if parts.len() != 3 {
        println!("Invalid expression! Please use format: number operator number");
        return;
    }

    let a: f64 = match parts[0].parse() {
        Ok(num) => num,
        Err(_) => {
            println!("First value is not a valid number");
            return;
        }
    };

    let operator = parts[1];

    let b: f64 = match parts[2].parse() {
        Ok(num) => num,
        Err(_) => {
            println!("Second value is not a valid number");
            return;
        }
    };

    let result = match operator {
        "+" => a + b,
        "-" => a - b,
        "*" => a * b,
        "/" => {
            if b == 0.0 {
                println!("Error: Division by zero");
                return;
            }
            a / b
        },
        _ => {
            println!("Unknown operator: {}", operator);
            return;
        }
    };

    println!("Result: {} {} {} = {}", a, operator, b, result);
}
